import React from 'react';
import WeatherCard from './weatherCard';
//api
// import Select, { components } from 'react-select';

// class API {
// //https://api.openweathermap.org/data/2.5/onecall/timemachine?lat=50.100193&lon=53.195873&dt=1621026506&appid=7502839bf5a559b85bd11512693a5993

// }


export default function WeatherPast(){

    const [dataForReq, setDataForReq] = React.useState({
        lat: '',
        lon: '',
        dt: ''
    });

    const [cords, setCords] = React.useState([]);
    const [minDate, setMinDate] = React.useState('');
    const [maxDate, setMaxDate] = React.useState('');
    const [weatherInfo, setWeatherInfo] = React.useState({
        date: '',
        weather: ''
    })

    function reqToApi(lat, lon, dt) {
        console.log('18', lat, lon);
        return fetch(`https://api.openweathermap.org/data/2.5/onecall/timemachine?lat=${lat}&lon=${lon}&dt=${dt}&appid=7502839bf5a559b85bd11512693a5993`)
        .then(res => res.json())
        .then(data => setWeatherInfo({...weatherInfo,
            date: dt,
            weather: data.current.temp}))
        .catch(err => console.log(err))
    }

    React.useEffect(() => {

    }, [weatherInfo])

    React.useEffect(() => {
        handleMinDate();
        handleMaxDate();
    }, [minDate, maxDate])


     React.useEffect(() => {
        const {lat, lon, dt } = dataForReq;
        if(lat && lon && dt ){
            reqToApi(lat, lon, dt);
        }
    }, [dataForReq])

    function handleChange(e){
        const {lat, lon, dt } = dataForReq;

        if (e.target === document.querySelector('input[type=date]')) {
            const dtVal = e.target.value;
            const dtValUnix = new Date(dtVal).getTime() / 1000;
            setDataForReq({...dataForReq, 
                            dt:dtValUnix})

        } else if(e.target === document.querySelector('select[type=text]')){
                const latVal = e.target[e.target.selectedIndex].attributes.lat.value;
                const lonVal = e.target[e.target.selectedIndex].attributes.lon.value;
                setDataForReq({...dataForReq, 
                    lat:latVal,
                    lon:lonVal})
        }
    }

    function handleMinDate(){
        let date = new Date();
        const minDateUnix =  date.setDate(date.getDate()-5); //unix time
        let minDateUtc = new Date(minDateUnix);
        setMinDate(`${minDateUtc.getFullYear()}-0${minDateUtc.getMonth()+1}-${minDateUtc.getDate()}`);
    }

    function handleMaxDate() {
        let date = new Date();
        setMaxDate(`${date.getFullYear()}-0${date.getMonth()+1}-${date.getDate()}`);
    }
    return (
        <>
        <h1>Weather Past</h1>
        <div className="form">
            <form action="">
                <select type="text" onChange={handleChange}>
                <option disabled>Выберите город</option>
                    <option lat={'53.195873'} lon={'50.100193'}>Самара</option>
                    <option lat={'53.507836'} lon={'49.420393'}>Тольятти</option>
                    <option lat={'51.533557'} lon={'46.034257'}>Саратов</option>
                </select>
                <input type="date" min={minDate} max={maxDate} onChange={handleChange}/>
                <WeatherCard info = {weatherInfo} />
            </form>
        </div>
        </>

    )
}
