import React from 'react';
import weatherCard from './weatherCard';
//api
// import Select, { components } from 'react-select';

// class API {
// //https://api.openweathermap.org/data/2.5/onecall/timemachine?lat=50.100193&lon=53.195873&dt=1621026506&appid=7502839bf5a559b85bd11512693a5993

// }


export default function WeatherPast(){

    const [dataForReq, setDataForReq] = React.useState({
        lat: '',
        lon: '',
        dt: ''
    });

    const [cords, setCords] = React.useState([]);
    const [minDate, setMinDate] = React.useState('');
    const [maxDate, setMaxDate] = React.useState('');
    const [weatherInfo, setWeatherInfo] = React.useState({
        date: '',
        weather: ''
    })

    function reqToApi(lat, lot) {
        console.log('18', lat, lot);
        return fetch(`https://api.openweathermap.org/data/2.5/onecall/timemachine?lat=${lat}&lon=${lot}&dt=1621461219&appid=7502839bf5a559b85bd11512693a5993`)
        .then(res => res.json())
        .then(data => console.log(data))
        .catch(err => console.log(err))
    }

    React.useEffect(() => {
        if(cords.length>0){
            const [lat, lot] = cords.split(', ')
            reqToApi(lat, lot);
        }
    }, [cords])

    React.useEffect(() => {
        handleMinDate();
        handleMaxDate();
    }, [minDate, maxDate])

    function handleChange(e){
        const {lat, lon, dt } = dataForReq;
        console.log(e.target);
        setCords(e.target.value);
        console.log(cords);
    }

    function handleMinDate(){
        let date = new Date();
        const minDateUnix =  date.setDate(date.getDate()-5); //unix time
        let minDateUtc = new Date(minDateUnix);
        setMinDate(`${minDateUtc.getFullYear()}-0${minDateUtc.getMonth()+1}-${minDateUtc.getDate()}`);
    }

    function handleMaxDate() {
        let date = new Date();
        setMaxDate(`${date.getFullYear()}-0${date.getMonth()+1}-${date.getDate()}`);
    }
    return (
        <>
        <h1>Weather Past</h1>
        <div className="form">
            <form action="">
                <select type="text" onChange={handleChange}>
                    <option value={'53.195873, 50.100193'}>Самара</option>
                    <option value={'53.507836, 49.420393'}>Тольятти</option>
                    <option value={'51.533557, 46.034257'}>Саратов</option>
                </select>
                <input type="date" min={minDate} max={maxDate} onChange={handleChange}/>
                <weatherCard />
            </form>
        </div>
        </>

    )
}
