export default class Api {

    constructor(url, lat, lon, dt, key){
        this.url = url;
        this.lat = lat;
        this.lon = lon;
        this.dt = dt;
        this.Apikey = key;
    }

    handleOriginalResponse(res) {
        if (!res.ok) {
            return Promise.reject(`Error: ${res.status}`);
        }
        return res.json();
    }

